# Navigate to the `tools` directory.
cd tools

# Generate random programs and profile DEAP.
python deap/profile.py

# Convert the random programs generated by DEAP 
# into forms useable by the other relevant GP tools.
python convert/convert.py

# Profile TensorGP.
cd tensorgp
python profile.py

# Profile Operon.
cd ../operon/operon/build
./operon-test --tc="Node Evaluations Batch"

